function injectValidate(noPass, domName) {
  const checkTypeMsg = `<p style="color:red" id="domErrMsg">
          偵測到欄位中含有不支援之特殊字型或符號，需請您調整修正文字
          <a style="font-weight:900 color:#1E8ACC" href="http://www.google.com">
            (詳細規範)
          </a>
        </p>`;

  const errElementDom = document.querySelector("#domErrMsg");

  // console.log("errElementDom", !!errElementDom);

  if (!noPass) {
    document.querySelector(domName).innerHTML = checkTypeMsg;
  } else if (!!errElementDom && noPass) {
    errElementDom.parentNode.removeChild(errElementDom);
  }
}

